package TestPackage;

public class DemoOfIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=120;     //Variable declaration & initialization
			int b=210;   	//Variable declaration & initialization
			
			if(a>b)    //use of if statetment
			{
		System.out.println("A is greater than B");	//Executes only if the condition is true
	}
			if(a<b)
			{
				System.out.println("A is less than B");	//Executes only if the condition is true
			}
			
	}}


